def draw():
    ax.plot([100, 0], [200, 0], [-100, -100], c="b")
    ax.plot([0, 200], [0, 0], [-100, -100], c="b")
    ax.plot([100, 200], [200, 0], [-100, -100], c="b")
    plt.show()

def Ray(start=None, direction=None):
    p1 = start
    p2 = [start[0] + direction[0], start[1] + direction[1], start[2] + direction[2]]
    ax.plot([p1[0], p2[0]], [p1[1], p2[1]], [p1[2], p2[2]], c="r")

